import React from "react";
import './Tarjeta.css'

const Tarjeta = (props) => {
  const { agregarAlCarro, producto } = props;

  return (
    <div className="tarjeta">
      <img src={producto.img} alt="" />
      <p>{producto.name}</p>
      <p>{producto.price}</p>
    </div>
  );
};

export default Tarjeta;
