import React from 'react';
import './Bubble.css'

const Bubble = (props) => {
    const { value } = props;

    const getNumber = (n) => {
        if (!n) {
            return ''
        }
        return n > 9 ? '9+' : n
    }

    return (
        <span className="bubbleAlert">{getNumber()}</span>
    );
};

export default Bubble;
