import React from 'react';
import Bubble from './Bubble';
import './Carro.css'

const Carro = (props) => {
    const { carro } = props;

    const getValue = (carro) => { 
        const value = 0
        carro.map(producto => value += producto.cantidad)
        return value
    }

    return (
        <div>
            <span className="bubble">
                {getValue(carro) > 0 ?
                <Bubble value={carro.length}/>
                : ''
                }
            </span>
            <button className='carroButton'>
                Carro
            </button>
        </div>
    );
};

export default Carro;
