import { useState } from 'react';

import './App.css';
import Layout from './Components/Layout/Layout';
import Title from './Components/Title/Title'
import NavBar from './Components/NavBar/NavBar';
import ListaDeTarjetas from './Components/ListaDeTarjetas/ListaDeTarjetas'

function App() {
  const [productos, setProductos] = useState[
    { name: 'Print', price: 1200, img: '/images/6328.png' },
    { name: 'Print1', price: 1500, img: '/images/Brigadoon.jpeg' },
    { name: 'Print2', price: 1300, img: '/images/floral-prints-.jpeg' }
  ];
  const [carro, setCarro] = useState({});

  const state = {
    productos: [

    ],
    carro: [
      //{ name: 'Print1', price: 1500, img: '/images/Brigadoon.jpeg', cantidad: 1 },
    ],
  }

  const agregarAlCarro = (producto) => {
    return setState({
      carro: state.carro.concat({ ...producto, cantidad: 1 })
    })
  }

  return (
    <div>
      <NavBar />
      <Layout>
        <Title />
        <ListaDeTarjetas
          agregarAlCarro={() => { agregarAlCarro() }}
          productos={state.productos}
        />
      </Layout>

    </div>
  );
}

export default App;
